/**
 * Replaces all accents with their base characters in a case sensitive manner
 */
export const normalize = (text) => {
    const regexp = new RegExp(`[${Object.keys(CHAR_MAP).join('')}]`, 'g');
    return text.replace(regexp, (match) => CHAR_MAP[match] || match);
};
